import { useState, useEffect } from "react";
import { parseGIF, decompressFrames } from "gifuct-js";
import '../stylesheets/Frames.css';

export default function DecryptSection() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [error, setError] = useState(null);

  const handleGIFChange = (e) => {
    const gifFile = e.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError("Please upload a valid GIF file");
      return;
    }
    setFile(gifFile);
    setFileName(gifFile.name);
    setError(null);
  };

  const extractMetadataBits = async (gifFile) => {
    const arrayBuffer = await gifFile.arrayBuffer();
    const gif = parseGIF(arrayBuffer);
    const frames = decompressFrames(gif, true);

    // Extract LSBs from first pixel of each frame
    let allBits = '';
    for (let i = 0; i < frames.length; i++) {
      allBits += (frames[i].patch[0] & 1).toString();
    }

    // Get metadata length from first 32 bits
    const lengthBits = allBits.slice(0, 32);
    const metadataLength = parseInt(lengthBits, 2);

    // Extract metadata bits
    const metadataBits = allBits.slice(32, 32 + metadataLength);

    console.log("Metadata length (bits):", metadataLength);
    console.log("Raw metadata bits:", metadataBits);

    return metadataBits;
  };

  const handlePrintMetadata = () => {
    if (!file) return;
    extractMetadataBits(file).catch(err => setError(err.message));
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      {file && (
        <button onClick={handlePrintMetadata}>
          Print Metadata Bits to Console
        </button>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
