import { useState } from "react";
import ToBits from "./ToBits";
import Hamming from "hamming-code"; 

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1];

function bitArrayToUint8Array(bits) {
	const bytes = [];
	for (let i = 0; i < bits.length; i += 8) {
		let byte = 0;
		for (let j = 0; j < 8; j++) {
			byte = (byte << 1) | (bits[i + j] || 0);
		}
		bytes.push(byte);
	}
	return new Uint8Array(bytes);
}

// Extract all bits (metadata + payload) from GIF frames
function extractAllBitsFromFrames(frames) {
	const allBits = [];
	for (const frame of frames) {
		const patch = frame.patch;
		for (let i = 0; i < patch.length; i += 4) {
			allBits.push(patch[i + 2] & 1); // LSB of blue channel
		}
	}

	// Check header
	for (let i = 0; i < HEADER_BITS.length; i++) {
		if (allBits[i] !== HEADER_BITS[i]) {
			throw new Error("Header not found. Possibly not encrypted or corrupt.");
		}
	}

	// Return ALL bits including header
	return allBits;
}

export default function Decryption() {
	const [gifFile, setGifFile] = useState(null);
	const [frames, setFrames] = useState([]);
	const [error, setError] = useState(null);

	const handleGIFChange = (e) => {
		const file = e.target.files[0];
		if (!file) return;
		setGifFile(file);

		ToBits(file)
			.then(({ frames }) => setFrames(frames))
			.catch((err) => setError(err.message));
	};

const handleDownloadMessageBits = () => {
	if (!frames.length) return;

	try {
		const allBits = extractAllBitsFromFrames(frames);

		// Extract metadata length
		const metadataLengthBits = allBits.slice(HEADER_BITS.length, HEADER_BITS.length + 32);
		const metadataLength = parseInt(metadataLengthBits.join(""), 2);
		const metadataEndIndex = HEADER_BITS.length + 32 + metadataLength;

		// Extract message length
		const messageLengthBits = allBits.slice(metadataEndIndex, metadataEndIndex + 32);
		const messageLength = parseInt(messageLengthBits.join(""), 2);
		const messageStartIndex = metadataEndIndex + 32;
		const messageEndIndex = messageStartIndex + messageLength;

		// Extract message bits
		const messageBits = allBits.slice(messageStartIndex, messageEndIndex);

		// --- Decode Hamming ECC to correct single-bit errors ---
		const correctedBits = Hamming.decode(messageBits);

		// --- Download corrected message bits ---
		const messageBitString = correctedBits.join("");
		const blobMessage = new Blob([messageBitString], { type: "text/plain" });
		const aMessage = document.createElement("a");
		aMessage.href = URL.createObjectURL(blobMessage);
		aMessage.download = "gif_message_bits.txt";
		aMessage.click();

		// --- Convert corrected bits to UTF-8 text and download ---
		const messageBytes = [];
		for (let i = 0; i < correctedBits.length; i += 8) {
			let byte = 0;
			for (let j = 0; j < 8; j++) {
				byte = (byte << 1) | (correctedBits[i + j] || 0);
			}
			messageBytes.push(byte);
		}
		const messageText = new TextDecoder("utf-8").decode(new Uint8Array(messageBytes));
		const blobText = new Blob([messageText], { type: "text/plain" });
		const aText = document.createElement("a");
		aText.href = URL.createObjectURL(blobText);
		aText.download = "gif_message_text.txt";
		aText.click();

		console.log("Message bits and text downloaded successfully.");
	} catch (err) {
		setError(err.message);
	}
};


	return (
		<div>
			<h2>Decrypt GIF Binary Data</h2>
			<input type="file" accept="image/gif" onChange={handleGIFChange} />
			{gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
			{frames.length > 0 && (
				<button onClick={handleDownloadMessageBits}>
					Download Message Bits
				</button>
			)}
			{error && <p className="error">{error}</p>}
		</div>
	);
}
