import { useState, useEffect } from "react";
import { parseGIF, decompressFrames } from "gifuct-js";

export default function GifFrameViewer({ file }) {
  const [framePreviews, setFramePreviews] = useState([]);

  useEffect(() => {
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      const arrayBuffer = reader.result;

      const gif = parseGIF(arrayBuffer);
      const frames = decompressFrames(gif, true);

      const previews = [];

      // Convert each frame to an image preview
      frames.forEach((frame) => {
        const { width, height, patch } = frame;

        if (isNaN(width) || isNaN(height) || width <= 0 || height <= 0) {
          console.error('Invalid frame dimensions:', width, height);
          return;
        }

        // Create a canvas
        const canvas = document.createElement("canvas");
        canvas.width = width;
        canvas.height = height;
        const ctx = canvas.getContext("2d");

        // Copy RGBA pixels into canvas
        const imageData = ctx.createImageData(width, height);
        imageData.data.set(patch);
        ctx.putImageData(imageData, 0, 0);

        // Canvas → PNG data URL
        const url = canvas.toDataURL("image/png");
        previews.push(url);
      });

      setFramePreviews(previews);
    };

    reader.readAsArrayBuffer(file);
  }, [file]);

  return (
    <div>
      <h2>GIF Frame Preview</h2>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)",
          gap: "15px",
        }}
      >
        {framePreviews.map((src, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "5px",
              textAlign: "center",
              background: "#fafafa",
            }}
          >
            <p style={{ marginBottom: "5px" }}>Frame {index}</p>
            <img
              src={src}
              alt={`Frame ${index}`}
              style={{ width: "100%", borderRadius: "4px" }}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
