// toBinary.js
const toBinary = () => {
  const fileInput = document.createElement('input');
  fileInput.type = 'file';
  fileInput.id = 'file-input';

  const convertButton = document.createElement('button');
  convertButton.textContent = 'Convert to Binary';
  convertButton.id = 'convert-button';

  const binaryOutput = document.createElement('div');
  binaryOutput.id = 'binary-output';

  const container = document.createElement('div');
  container.appendChild(fileInput);
  container.appendChild(convertButton);
  container.appendChild(binaryOutput);

  convertButton.addEventListener('click', async () => {
    const file = fileInput.files[0];
    if (!file) {
      alert('Please select a file');
      return;
    }

    try {
      const arrayBuffer = await file.arrayBuffer();
      const uint8Array = new Uint8Array(arrayBuffer);
      const binaryString = Array.from(uint8Array).map(byte => byte.toString(2).padStart(8, '0')).join('');
      binaryOutput.textContent = binaryString;
    } catch (error) {
      console.error(error);
      alert('Error converting file to binary');
    }
  });

  return container;
};

export default toBinary;
