// Decryption.js
import { useEffect, useState } from "react";
import ToBits from "./ToBits";
import "../stylesheets/Frames.css";
import "../stylesheets/Popup.css";

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1];
const PIXELS_PER_CHUNK = 500;

export default function Decryption() {
  const [gifFile, setGifFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [popupData, setPopupData] = useState(null);
  const [error, setError] = useState(null);

  // ---- handle GIF upload ----
  const handleGIFChange = (e) => {
    const file = e.target.files[0];
    if (!file || (!file.type.includes("gif") && !file.name.endsWith(".gif"))) {
      setError("Please upload a valid GIF");
      return;
    }
    setError(null);
    setGifFile(file);

    ToBits(file)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  // ---- extract encrypted bits and convert to RGBA ----
  const handleExtract = () => {
    if (!frames.length) return;

    const firstFrame = frames[0];
    // check header
    let headerMatches = true;
    for (let i = 0; i < HEADER_BITS.length; i++) {
      if ((firstFrame.patch[i * 4 + 2] & 1) !== HEADER_BITS[i]) {
        headerMatches = false;
        break;
      }
    }

    if (!headerMatches) {
      setError("Header not found in GIF.");
      return;
    }

    // Extract all bits from frames (skip header in first frame)
    let bits = [];
    for (let frameIndex = 0; frameIndex < frames.length; frameIndex++) {
      const frame = frames[frameIndex];
      const startPixel = frameIndex === 0 ? HEADER_BITS.length : 0;
      for (let i = startPixel * 4; i < frame.patch.length; i += 4) {
        bits.push(frame.patch[i + 2] & 1);
      }
    }

    // Reconstruct RGBA pixel values from bits
    const reconstructedFrames = [];
    let bitIndex = 0;

    frames.forEach(frame => {
      const newPatch = new Uint8ClampedArray(frame.patch.length);
      for (let i = 0; i < frame.patch.length; i += 4) {
        for (let j = 0; j < 4; j++) {
          if (j === 2) { // blue channel LSB
            newPatch[i + j] = (frame.patch[i + j] & ~1) | (bits[bitIndex] ?? 0);
            bitIndex++;
          } else {
            newPatch[i + j] = frame.patch[i + j];
          }
        }
      }
      reconstructedFrames.push({ ...frame, patch: newPatch });
    });

    // Precompute pixel data for popup
    const pixelArray = [];
    const frame = reconstructedFrames[0]; // show first frame in popup
    const pixels = frame.patch;
    for (let i = 0; i < pixels.length; i += 4) {
      const r = pixels[i];
      const g = pixels[i + 1];
      const b = pixels[i + 2];
      const a = pixels[i + 3];
      const rgbaString = `rgba(${r}, ${g}, ${b}, ${a / 255})`;
      pixelArray.push({
        rgba: rgbaString,
        values: [r, g, b, a],
      });
    }

    setPopupData({
      frameIndex: 0,
      pixels: pixelArray,
      visibleCount: PIXELS_PER_CHUNK,
      reconstructedFrames,
    });
  };

  // ---- load more pixels as user scrolls ----
  const handleScroll = (e) => {
    const target = e.target;
    if (target.scrollTop + target.clientHeight >= target.scrollHeight - 20) {
      setPopupData((prev) =>
        prev
          ? {
              ...prev,
              visibleCount: Math.min(
                prev.visibleCount + PIXELS_PER_CHUNK,
                prev.pixels.length
              ),
            }
          : prev
      );
    }
  };

  // ---- export decrypted RGBA to TXT ----
  const exportRGBA = () => {
    if (!popupData || !popupData.reconstructedFrames) return;

    let content = '';
    popupData.reconstructedFrames.forEach((frame, fIndex) => {
      content += `Frame ${fIndex}:\n`;
      const pixels = frame.patch;
      for (let i = 0; i < pixels.length; i += 4) {
        const r = pixels[i];
        const g = pixels[i + 1];
        const b = pixels[i + 2];
        const a = pixels[i + 3];
        content += `${r}, ${g}, ${b}, ${a}\n`;
      }
      content += '\n';
    });

    const blob = new Blob([content], { type: 'text/plain' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'decrypted_pixels.txt';
    a.click();
    URL.revokeObjectURL(a.href);
  };

  return (
    <div>
      <h2>Decrypt GIF</h2>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
      {frames.length > 0 && <button onClick={handleExtract}>Decrypt</button>}
      {popupData && (
        <>
          <button onClick={exportRGBA}>Export Decrypted RGBA</button>
          <div className="popup-overlay" onClick={() => setPopupData(null)}>
            <div className="popup-content" onClick={(e) => e.stopPropagation()} onScroll={handleScroll}>
              <h3>Frame {popupData.frameIndex} RGBA Values</h3>
              <div className="pixel-grid">
                {popupData.pixels
                  .slice(0, popupData.visibleCount)
                  .map((p, i) => (
                    <div key={i} className="pixel-item">
                      <div className="pixel-color" style={{ background: p.rgba }} />
                      <span>Pixel {i}: [{p.values.join(", ")}]</span>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
}
