import { useState, useEffect } from "react";
import ToBits from "./ToBits";
import '../stylesheets/Frames.css';

const decryptMetadataBits = (frames) => {
  let bits = '';
  for (let i = 0; i < frames.length; i++) {
    bits += (frames[i].patch[2] & 1).toString();
  }
  return bits;
};

export default function DecryptSection() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [frames, setFrames] = useState([]);
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleGIFChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (file.type !== 'image/gif') {
      setError("Please upload a valid GIF file");
      return;
    }
    setFile(file);
    setFileName(file.name);
    setError(null);
  };

  const handlePrintMetadata = () => {
    if (!frames.length) return;

    // --- Step 1: extract LSBs from first pixel of each frame ---
    const metadataBits = decryptMetadataBits(frames);

    // --- Step 2: read the 32-bit length header (in bits) ---
    const lengthBits = metadataBits.slice(0, 32);
    const metadataLength = parseInt(lengthBits, 2); // number of bits of metadata

    // --- Step 3: extract the exact metadata bits ---
    let actualMetadataBits = metadataBits.slice(32, 32 + metadataLength);

    // --- Step 4: truncate to full bytes ---
    const usableBitsLength = Math.floor(actualMetadataBits.length / 8) * 8;
    actualMetadataBits = actualMetadataBits.slice(0, usableBitsLength);

    // --- Step 5: convert bits to string ---
    let metadataStr = '';
    for (let i = 0; i < actualMetadataBits.length; i += 8) {
      const byte = actualMetadataBits.slice(i, i + 8);
      metadataStr += String.fromCharCode(parseInt(byte, 2));
    }

    // --- Step 6: parse JSON safely ---
    try {
      const metadata = JSON.parse(metadataStr);
      console.log("Decrypted Metadata:", metadata);
    } catch (err) {
      console.error("Failed to parse metadata JSON:", err);
      console.log("Raw metadata string:", metadataStr);
    }
  };

  useEffect(() => {
    if (!file) return;
    ToBits(file)
      .then(({ frames: extractedFrames }) => setFrames(extractedFrames))
      .catch(err => setError(err.message));
  }, [file]);

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      <input
        type="password"
        placeholder="Enter password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {file && password && frames.length > 0 && (
        <button onClick={handlePrintMetadata}>Print Metadata to Console</button>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
