// Encryption.js
import { useEffect, useState } from "react";
import ToGIF from "./ToGIF";
import ToBits from "./ToBits";
import ToBinary from "./ToBinary";
import "../stylesheets/Frames.css";
import "../stylesheets/Popup.css";

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1]; // 8-bit sync header
const PIXELS_PER_CHUNK = 500; // number of pixels rendered at once in popup

// ---- frame preview generator ----
async function generateFramePreviews(frames, setFramePreviews) {
  const previews = [];
  for (let i = 0; i < frames.length; i++) {
    const frame = frames[i];
    const canvas = document.createElement("canvas");
    canvas.width = frame.width;
    canvas.height = frame.height;
    const ctx = canvas.getContext("2d");
    const imageData = ctx.createImageData(frame.width, frame.height);
    imageData.data.set(frame.patch);
    ctx.putImageData(imageData, 0, 0);
    previews.push(canvas.toDataURL("image/png"));
  }
  setFramePreviews(previews);
}

// ---- spatial encoder (1 bit per pixel, blue LSB) ----
function spatialEncode(frames, bitstream) {
  const encodedFrames = [...frames];
  let bitIndex = 0;

  for (const frame of encodedFrames) {
    const patch = frame.patch;
    for (let i = 0; i < patch.length; i += 4) {
      if (bitIndex >= bitstream.length) break;
      patch[i + 2] = (patch[i + 2] & ~1) | bitstream[bitIndex];
      bitIndex++;
    }
    if (bitIndex >= bitstream.length) break;
  }
  return encodedFrames;
}

// ---- calculate GIF bit capacity ----
function getCapacity(frames) {
  return frames.reduce((sum, f) => sum + f.patch.length / 4, 0);
}

// ---- main component ----
export default function Encryption() {
  const [gifFile, setGifFile] = useState(null);
  const [messageFile, setMessageFile] = useState(null);
  const [password, setPassword] = useState("");
  const [frames, setFrames] = useState([]);
  const [framePreviews, setFramePreviews] = useState([]);
  const [payload, setPayload] = useState(null);
  const [error, setError] = useState(null);
  const [popupData, setPopupData] = useState(null); // { frameIndex, pixels, visibleCount }

  // ---- handle GIF upload ----
  const handleGIFChange = (e) => {
    const file = e.target.files[0];
    if (!file || (!file.type.includes("gif") && !file.name.endsWith(".gif"))) {
      setError("Please upload a valid GIF");
      return;
    }
    setError(null);
    setGifFile(file);
  };

  // ---- handle message file upload ----
  const handleMessageFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setMessageFile(file);
  };

  // ---- extract frames when GIF changes ----
  useEffect(() => {
    if (!gifFile) return;

    setFrames([]);
    setFramePreviews([]);
    setPayload(null);
    setError(null);

    ToBits(gifFile)
      .then(({ frames }) => setFrames(frames))
      .catch((err) => setError(err.message));
  }, [gifFile]);

  // ---- generate frame previews ----
  useEffect(() => {
    if (frames.length) generateFramePreviews(frames, setFramePreviews);
  }, [frames]);

  // ---- rebuild payload when message file or password changes ----
  useEffect(() => {
    if (!messageFile || !password) {
      setPayload(null);
      return;
    }

    (async () => {
      try {
        const encryptedPayload = await ToBinary(messageFile, password);

        if (frames.length) {
          const capacity = getCapacity(frames);
          const fullBitstream = [
            ...HEADER_BITS,
            ...encryptedPayload.metabits,
            ...encryptedPayload.bits,
          ];
          if (fullBitstream.length > capacity) {
            setError("Message too large for this GIF");
            setPayload(null);
            return;
          }
        }

        setPayload(encryptedPayload);
        setError(null);
      } catch (err) {
        setError(err.message);
        setPayload(null);
      }
    })();
  }, [messageFile, password, frames]);

  // ---- encrypt and download GIF ----
  const handleEncrypt = async () => {
    if (!frames.length || !payload) return;

    const fullBitstream = [
      ...HEADER_BITS,
      ...payload.metabits,
      ...payload.bits,
    ];

    const encodedFrames = spatialEncode(frames, fullBitstream);
    const gifURL = await ToGIF(encodedFrames);

    const a = document.createElement("a");
    a.href = gifURL;
    a.download = `encrypted_${gifFile.name}`;
    a.click();
  };

  // ---- open popup to inspect RGBA with precomputed color tiles ----
  const handleFrameClick = (frame, index) => {
    const pixels = frame.patch;
    const pixelArray = [];

    for (let i = 0; i < pixels.length; i += 4) {
      const r = pixels[i];
      const g = pixels[i + 1];
      const b = pixels[i + 2];
      const a = pixels[i + 3];
      const rgbaString = `rgba(${r}, ${g}, ${b}, ${a / 255})`;
      pixelArray.push({
        rgba: rgbaString,
        values: [r, g, b, a],
      });
    }

    setPopupData({
      frameIndex: index,
      pixels: pixelArray,
      visibleCount: PIXELS_PER_CHUNK,
    });
  };

  // ---- load more pixels as user scrolls ----
  const handleScroll = (e) => {
    const target = e.target;
    if (
      target.scrollTop + target.clientHeight >=
      target.scrollHeight - 20
    ) {
      setPopupData((prev) =>
        prev
          ? {
              ...prev,
              visibleCount: Math.min(
                prev.visibleCount + PIXELS_PER_CHUNK,
                prev.pixels.length
              ),
            }
          : prev
      );
    }
  };

  return (
    <div>
      <h2>GIF Message Encryption</h2>

      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      <input type="file" onChange={handleMessageFileChange} />
      <input
        type="password"
        placeholder="Encryption password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {frames.length > 0 && <p>{frames.length} frames extracted from GIF</p>}

      {payload && frames.length > 0 && (
        <button onClick={handleEncrypt}>Encrypt Message</button>
      )}

      {payload && frames.length > 0 && (
        <p>
          Message uses {payload.bits.length + payload.metabits.length} bits of{" "}
          {getCapacity(frames)} available
        </p>
      )}

      {error && <p className="error">{error}</p>}

      {/* frame previews */}
      {framePreviews.length > 0 && (
        <div className="frames">
          {framePreviews.map((src, i) => (
            <img
              key={i}
              src={src}
              className="canvas"
              style={{ cursor: "pointer", marginBottom: "10px" }}
              onClick={() => handleFrameClick(frames[i], i)}
              alt={`Frame ${i}`}
            />
          ))}
        </div>
      )}

      {/* RGBA popup with lazy loading */}
      {popupData && (
        <div
          className="popup-overlay"
          onClick={() => setPopupData(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            className="popup-content"
            onClick={(e) => e.stopPropagation()}
            onScroll={handleScroll}
            style={{
              background: "#ffffff",
              padding: "20px",
              maxHeight: "80%",
              width: "80%",
              borderRadius: "8px",
              overflowY: "auto",
            }}
          >
            <h3>Frame {popupData.frameIndex} RGBA Values</h3>
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill, minmax(120px, 1fr))",
                gap: "4px",
                fontSize: "12px",
                fontFamily: "monospace",
              }}
            >
              {popupData.pixels
                .slice(0, popupData.visibleCount)
                .map((p, i) => (
                  <div
                    key={i}
                    style={{
                      display: "flex",
                      alignItems: "center",
                      padding: "2px",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      gap: "6px",
                    }}
                  >
                    <div
                      style={{
                        width: "16px",
                        height: "16px",
                        background: p.rgba,
                        border: "1px solid #000",
                      }}
                    />
                    <span>Pixel {i}: [{p.values.join(", ")}]</span>
                  </div>
                ))}
            </div>
            <button
              onClick={() => setPopupData(null)}
              style={{ marginTop: "10px" }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
