import React, { useState } from 'react';

export default function ToBinary() {
  const [file, setFile] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [binaryString, setBinaryString] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleConvert = async () => {
    if (!file) {
      alert('Please select a file');
      return;
    }

    try {
      const arrayBuffer = await file.arrayBuffer();
      const uint8Array = new Uint8Array(arrayBuffer);
      const binaryString = Array.from(uint8Array).map(byte => byte.toString(2).padStart(8, '0')).join('');
      setBinaryString(binaryString);
      setShowPopup(true);
    } catch (error) {
      console.error(error);
      alert('Error converting file to binary');
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleConvert}>Convert to Binary</button>
      {showPopup && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '10px',
            boxShadow: '0px 0px 10px rgba(0,0,0,0.2)',
            maxWidth: '80%',
            maxHeight: '80%',
            overflowY: 'auto',
          }}>
            <h2>Binary String:</h2>
            <pre style={{
              whiteSpace: 'pre-wrap',
            }}>{binaryString}</pre>
            <button onClick={handleClosePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
