import { encryptData } from "./ToBlackBox";

// Convert Uint8Array to bit array
function uint8ArrayToBitArray(bytes) {
  const bits = [];
  for (const byte of bytes) {
    for (let i = 7; i >= 0; i--) {
      bits.push((byte >> i) & 1);
    }
  }
  return bits;
}

// Convert string to bit array
function stringToBitArray(str) {
  const bits = [];
  for (let i = 0; i < str.length; i++) {
    const byte = str.charCodeAt(i);
    for (let j = 7; j >= 0; j--) {
      bits.push((byte >> j) & 1);
    }
  }
  return bits;
}

// Convert number to 32-bit array
function numberTo32BitArray(num) {
  const bits = [];
  for (let i = 31; i >= 0; i--) {
    bits.push((num >> i) & 1);
  }
  return bits;
}

export default async function ToBinary(file, password) {
  if (!file) throw new Error("No file provided");
  if (!password || password.trim() === "") throw new Error("Password is required");

  const arrayBuffer = await file.arrayBuffer();
  const bytes = new Uint8Array(arrayBuffer);

  // Encrypt raw bytes
  const { encryptedData, iv, salt } = await encryptData(bytes, password);

  // Convert encrypted payload to bits
  const payloadBits = uint8ArrayToBitArray(encryptedData);

  // Metadata object
  const metadata = {
    iv: btoa(String.fromCharCode(...iv)),
    salt: btoa(String.fromCharCode(...salt)),
    originalLength: bytes.length,
    fileName: file.name,
    fileType: file.type
  };

  // Convert metadata to JSON and then Base64
  const metadataBase64 = btoa(JSON.stringify(metadata));
  const metadataBits = stringToBitArray(metadataBase64);

  // Add 32-bit length header
  const lengthBits = numberTo32BitArray(metadataBits.length);
  const fullMetadataBits = [...lengthBits, ...metadataBits];

  console.log("Metadata object:", metadata);
  console.log("Metadata Base64 string:", metadataBase64);

  return {
    bits: payloadBits,
    metabits: fullMetadataBits
  };
}
