// Home.js
import { useState } from "react";
import "../stylesheets/Home.css";
import GifFrameViewer from "../components/GifFrameViewer";

function Home() {
  const [selectedGifFile, setSelectedGifFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.type !== "image/gif") {
      alert("Please upload a GIF file.");
      return;
    }

    setSelectedGifFile(file);
  };

  return (
    <div className="home-container">
      <header className="home-header">
        <h1 className="home-title">Delving into Steganography</h1>
        <p className="home-subtitle">Explore & discover new ideas.</p>
      </header>

      <section className="home-cards">
        <div className="home-card">
          <h2>Project</h2>
        </div>

        <div className="home-card">
          <h2>Contact</h2>
        </div>
      </section>

      {/* ðŸŸ¦ GIF Upload + Frame Preview Outside Cards */}
      <div className="gif-section">
        <h2>GIF Frame Preview</h2>

        {/* GIF Upload Input */}
        <input
          type="file"
          accept="image/gif"
          onChange={handleFileChange}
          className="gif-input"
        />

        {/* Preview all frames */}
        {selectedGifFile && <GifFrameViewer file={selectedGifFile} />}
      </div>
    </div>
  );
}

export default Home;
