import React, { useEffect, useState } from 'react';
import { parseGIF, decompressFrames } from 'gifuct-js';

const GifPreview = () => {
  const [gifBuffer, setGifBuffer] = useState(null);
  const [frames, setFrames] = useState([]);

  useEffect(() => {
    fetch('path/to/your/gif.gif')
      .then(response => response.arrayBuffer())
      .then(buffer => setGifBuffer(buffer));
  }, []);

  useEffect(() => {
    if (gifBuffer) {
      const gif = parseGIF(gifBuffer);
      const decompressedFrames = decompressFrames(gif, true);
      const extractedFrames = [];

      decompressedFrames.forEach((frame, index) => {
        const { pixels, dims } = frame;
        const imageData = new Uint8ClampedArray(pixels.length * 4);

        for (let i = 0; i < pixels.length; i += 1) {
          const pixelIndex = pixels[i];
          const colorIndex = gif.gct[pixelIndex];
          const { r, g, b } = colorIndex;

          let a = 255;
          if (gif.gct.transparency && pixelIndex === gif.gct.transparency.index) {
            a = 0;
          }

          imageData[i * 4] = r;
          imageData[i * 4 + 1] = g;
          imageData[i * 4 + 2] = b;
          imageData[i * 4 + 3] = a;
        }

        const canvas = document.createElement('canvas');
        canvas.width = dims.width;
        canvas.height = dims.height;
        const ctx = canvas.getContext('2d');
        const imageDataObj = new ImageData(imageData, dims.width, dims.height);
        ctx.putImageData(imageDataObj, 0, 0);

        extractedFrames.push({
          index,
          canvas: canvas.toDataURL(),
          delay: frame.delay,
        });
      });

      setFrames(extractedFrames);
    }
  }, [gifBuffer]);

  return (
    <div>
      {frames.map((frame, index) => (
        <div key={index}>
          <img src={frame.canvas} alt={`Frame ${index}`} />
          <p>Delay: {frame.delay}ms</p>
        </div>
      ))}
    </div>
  );
};

export default GifPreview;
