import { useState } from "react";
import ToBits from "./ToBits";

const OPEN_BRACE_COUNT = 112; // stop after this many '{'

export default function DecryptMetadata() {
  const [file, setFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataText, setMetadataText] = useState('');
  const [error, setError] = useState(null);

  // Load GIF and convert to frames
  const handleGIFChange = (event) => {
    const gifFile = event.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setFile(gifFile);
    setError(null);

    ToBits(gifFile)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  const handleExtract = () => {
    if (!frames.length) return;

    // Step 1: Gather temporal bits (first pixel B of each frame)
    let bits = '';
    for (const frame of frames) {
      bits += (frame.patch[2] & 1).toString();
    }

    // Step 2: Convert bits to characters
    let text = '';
    for (let i = 0; i < bits.length; i += 8) {
      const byte = bits.slice(i, i + 8).padEnd(8, '0');
      text += String.fromCharCode(parseInt(byte, 2));
    }

    // Step 3: Find first '{' and extract until OPEN_BRACE_COUNT
    let openCount = 0;
    const startIndex = text.indexOf('{');
    if (startIndex === -1) {
      setError("No opening brace '{' found in metadata");
      return;
    }

    let endIndex = text.length;
    for (let i = startIndex; i < text.length; i++) {
      if (text[i] === '{') openCount++;
      if (openCount === OPEN_BRACE_COUNT) {
        endIndex = i + 1;
        break;
      }
    }

    const extracted = text.slice(startIndex, endIndex);
    setMetadataText(extracted);

    // Step 4: Download as .txt
    const blob = new Blob([extracted], { type: 'text/plain' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'extracted_metadata.txt';
    a.click();
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {file && <p>Uploaded GIF: {file.name}</p>}

      {frames.length > 0 && (
        <button onClick={handleExtract}>Extract Metadata</button>
      )}

      {metadataText && (
        <div>
          <p>Extracted Metadata:</p>
          <pre>{metadataText}</pre>
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
