import { useState } from "react";
import ToBits from "./ToBits";

export default function ExtractMetadata() {
  const [file, setFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataBits, setMetadataBits] = useState('');
  const [error, setError] = useState(null);

  const handleGIFChange = (event) => {
    const gifFile = event.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setError(null);
    setFile(gifFile);

    // Extract frames
    ToBits(gifFile)
      .then(({ frames }) => {
        setFrames(frames);
      })
      .catch(err => setError(err.message));
  };

  const handleExtract = () => {
    if (!frames.length) return;

    const length = 272; // number of metadata bits
    let bits = '';
    for (let i = 0; i < length; i++) {
      const frame = frames[i % frames.length]; // wrap around
      const patch = frame.patch;
      bits += (patch[2] & 1).toString(); // first pixel blue LSB
    }
    setMetadataBits(bits);

    // Convert bits to bytes
    const bytes = [];
    for (let i = 0; i < bits.length; i += 8) {
      const byte = bits.slice(i, i + 8).padEnd(8, '0'); // pad if needed
      bytes.push(parseInt(byte, 2));
    }

    // Create downloadable file
    const blob = new Blob([new Uint8Array(bytes)], { type: "application/octet-stream" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "extracted_metadata.bin";
    a.click();
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {file && <p>Uploaded GIF: {file.name}</p>}
      {frames.length > 0 && (
        <button onClick={handleExtract}>Extract Metadata</button>
      )}
      {metadataBits && (
        <div>
          <p>Metadata bits:</p>
          <pre>{metadataBits}</pre>
        </div>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
