import { useState } from "react";
import ToBits from "./ToBits";

const HEADER_PATTERN = "1010101111001101"; // same as encryption

export default function DecryptMetadata() {
  const [file, setFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataBits, setMetadataBits] = useState('');
  const [error, setError] = useState(null);

  // Load GIF and convert to frames
  const handleGIFChange = (event) => {
    const gifFile = event.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setFile(gifFile);

    ToBits(gifFile)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  // Automatically find where header starts
  const findStartIndex = (frames, headerPattern) => {
    const totalFrames = frames.length;
    for (let offset = 0; offset < totalFrames; offset++) {
      let bits = '';
      for (let i = 0; i < headerPattern.length; i++) {
        const frame = frames[(offset + i) % totalFrames];
        bits += (frame.patch[2] & 1).toString();
      }
      if (bits === headerPattern) return offset;
    }
    return 0; // fallback if header not found
  };

  const handleExtract = () => {
    if (!frames.length) return;

    // Find header
    const startIndex = findStartIndex(frames, HEADER_PATTERN);

    // Extract all bits after header
    let bits = '';
    for (let i = 0; i < frames.length; i++) {
      const frame = frames[(startIndex + i) % frames.length];
      bits += (frame.patch[2] & 1).toString();
    }

    const actualMetadataBits = bits.slice(HEADER_PATTERN.length);
    setMetadataBits(actualMetadataBits);

    // Format metadata for text output: 8 bits per group, space between bytes
    let formattedText = '';
    for (let i = 0; i < actualMetadataBits.length; i += 8) {
      const byte = actualMetadataBits.slice(i, i + 8).padEnd(8, '0');
      formattedText += byte + ' ';
      if ((i / 8 + 1) % 8 === 0) formattedText += '\n'; // newline every 8 bytes
    }

    // Download as .txt
    const blob = new Blob([formattedText], { type: "text/plain" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "extracted_metadata.txt";
    a.click();
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {file && <p>Uploaded GIF: {file.name}</p>}
      {frames.length > 0 && (
        <button onClick={handleExtract}>Extract Metadata</button>
      )}
      {metadataBits && (
        <div>
          <p>Metadata bits:</p>
          <pre>{metadataBits}</pre>
        </div>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
