// src/components/StegoTool.js
import { useState } from "react";
import ToBinary from "./toBinary";
import { encodeGIF, decodeGIF } from "./Stego";

export default function StegoTool() {
	const [binaryPayload, setBinaryPayload] = useState("");
	const [gifFrames, setGifFrames] = useState([]);
	const [key, setKey] = useState("secret");

	return (
		<div className="stego-tool">
			<h2>Steganography Tool</h2>

			<ToBinary
				onBinaryReady={(binary) => {
					console.log("Binary stored in StegoTool:", binary.length);
					setBinaryPayload(binary);
				}}
			/>

			<input
				type="text"
				placeholder="Encryption key"
				value={key}
				onChange={(e) => setKey(e.target.value)}
			/>

			<button
				onClick={() => {
					const encoded = encodeGIF(gifFrames, binaryPayload, key);
					setGifFrames(encoded);
				}}
			>
				Encode
			</button>
		</div>
	);
}
