// DecryptMetadataSection.js
import { useEffect, useState } from "react";
import ToBits from "./ToBits";
import '../stylesheets/Frames.css';

// Decode binary bits from frames
const decodeBits = (frames, totalBits) => {
  let bits = '';
  let frameIndex = 0;
  let pixelIndex = 0;

  while (bits.length < totalBits) {
    const frame = frames[frameIndex % frames.length];
    const patch = frame.patch;

    if (pixelIndex >= patch.length / 4) {
      pixelIndex = 0;
      frameIndex++;
      if (frameIndex >= frames.length) break; // avoid overflow
    }

    bits += (patch[pixelIndex * 4 + 2] & 1).toString(); // blue channel
    pixelIndex++;
  }

  return bits;
};

// Convert binary string to regular string
const binaryToString = (binaryStr) => {
  let str = '';
  for (let i = 0; i + 8 <= binaryStr.length; i += 8) {
    str += String.fromCharCode(parseInt(binaryStr.slice(i, i + 8), 2));
  }
  return str;
};

export default function DecryptMetadataSection() {
  const [frames, setFrames] = useState([]);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [metadata, setMetadata] = useState(null);
  const [error, setError] = useState(null);

  const handleGIFChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (file.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setError(null);
    setFile(file);
    setFileName(file.name);
  };

  useEffect(() => {
    if (!file) return;

    ToBits(file)
      .then(({ frames: extractedFrames }) => {
        setFrames(extractedFrames);

        try {
          // Step 1: Read first 32 bits = metadata length
          const headerBits = decodeBits(extractedFrames, 32);
          const metadataLength = parseInt(headerBits, 2);

          // Step 2: Read metadata bits
          const metadataBits = decodeBits(extractedFrames, 32 + metadataLength).slice(32);

          // Step 3: Convert bits → string → JSON
          const metadataStr = binaryToString(metadataBits);
          const parsed = JSON.parse(metadataStr);
          setMetadata(parsed);

        } catch (err) {
          setError("Failed to parse metadata: " + err.message);
        }
      })
      .catch(err => setError(err.message));
  }, [file]);

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      {metadata && (
        <div>
          <h2>Extracted Metadata</h2>
          <pre>{JSON.stringify(metadata, null, 2)}</pre>
        </div>
      )}

      {error && <p className="error">{error}</p>}
    </div>
  );
}
