import { useState } from "react";
import ToBits from "./ToBits";

export default function DecryptMetadata() {
  const [file, setFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataText, setMetadataText] = useState('');
  const [error, setError] = useState(null);

  const HEADER_PATTERN = "1010101111001101"; // must match encryption

  const handleGIFChange = (event) => {
    const gifFile = event.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setFile(gifFile);

    ToBits(gifFile)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  const findHeaderStart = (frames) => {
    for (let offset = 0; offset < frames.length; offset++) {
      let bits = '';
      for (let i = 0; i < HEADER_PATTERN.length; i++) {
        const frame = frames[(offset + i) % frames.length];
        bits += (frame.patch[2] & 1).toString();
      }
      if (bits === HEADER_PATTERN) return offset;
    }
    return -1;
  };

  const bitsToString = (bits) => {
    let str = '';
    for (let i = 0; i < bits.length; i += 8) {
      const byte = bits.slice(i, i + 8).padEnd(8, '0');
      str += String.fromCharCode(parseInt(byte, 2));
    }
    return str;
  };

  const handleExtract = () => {
    if (!frames.length) return;

    const startIndex = findHeaderStart(frames);
    if (startIndex === -1) {
      setError('Header not found in GIF.');
      return;
    }

    // Extract all bits after header
    let bits = '';
    for (let i = startIndex + HEADER_PATTERN.length; i < frames.length; i++) {
      const frame = frames[i % frames.length];
      bits += (frame.patch[2] & 1).toString();
    }

    let text = bitsToString(bits);

    // Cut off anything after the first closing brace }
    const braceIndex = text.indexOf('}');
    if (braceIndex !== -1) text = text.slice(0, braceIndex + 1);

    setMetadataText(text);

    // Download as .txt
    const blob = new Blob([text], { type: "text/plain" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "extracted_metadata.txt";
    a.click();
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {file && <p>Uploaded GIF: {file.name}</p>}
      {frames.length > 0 && <button onClick={handleExtract}>Extract Metadata</button>}
      {metadataText && (
        <div>
          <p>Metadata:</p>
          <pre>{metadataText}</pre>
        </div>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
