// Encodes / decodes uploaded files into GIFs using LSB steganography

import { parseGIF, decompressFrames } from "gifuct-js";
import ToBinary from "./ToBinary";
import { encodeGIF, decodeGIF } from "./gifStego";

/* =========================
   ENCODE
========================= */

export async function encodeFromUpload(gifFile, payloadFile, key = "") {
  if (!gifFile) throw new Error("No GIF file provided");
  if (!payloadFile) throw new Error("No payload file provided");

  // 1. Convert payload file → binary
  const binaryPayload = await ToBinary(payloadFile);

  // 2. Decode GIF → frames
  const gifBuffer = await gifFile.arrayBuffer();
  const gif = parseGIF(gifBuffer);
  const frames = decompressFrames(gif, true); // true = build patches

  // 3. Encode payload into frames
  const encodedFrames = encodeGIF(frames, binaryPayload, key);

  // 4. Return encoded frames (Home.js will rebuild GIF)
  return encodedFrames;
}

/* =========================
   DECODE
========================= */

export async function decodeFromUpload(gifFile, key = "") {
  if (!gifFile) throw new Error("No GIF file provided");

  // 1. Decode GIF → frames
  const gifBuffer = await gifFile.arrayBuffer();
  const gif = parseGIF(gifBuffer);
  const frames = decompressFrames(gif, true);

  // 2. Extract binary payload
  const binaryPayload = decodeGIF(frames, key);
  return binaryPayload;
}

