// DecryptSection.js

import { useEffect, useState } from "react";
import ToBits from "./ToBits";
import { decryptData } from "./ToBlackBox";
import '../stylesheets/Frames.css';

// Temporal decode for metadata
const temporalDecode = (frames, length) => {
  let metadataBits = '';
  let frameIndex = 0;

  for (let i = 0; i < length; i++) {
    const frame = frames[frameIndex % frames.length];
    metadataBits += (frame.patch[2] & 1).toString();
    frameIndex++;
  }

  return metadataBits;
};

// Convert binary string to text
const binaryToText = (binaryStr) => {
  let text = '';
  for (let i = 0; i < binaryStr.length; i += 8) {
    const byte = binaryStr.slice(i, i + 8);
    text += String.fromCharCode(parseInt(byte, 2));
  }
  return text;
};

export default function DecryptSection() {
  const [frames, setFrames] = useState([]);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [decryptedMessage, setDecryptedMessage] = useState('');

  const handleGIFChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (file.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setError(null);
    setFile(file);
    setFileName(file.name);
  };

  const handleDecryptClick = async () => {
    if (!frames.length || !password) {
      setError('File and password are required');
      return;
    }

    try {
      // Assume first 512 bits are metadata (adjust as needed)
      const metadataBits = temporalDecode(frames, 512);
      
      // Convert bits to Uint8Array
      const encryptedMetaBytes = new Uint8Array(metadataBits.match(/.{1,8}/g).map(b => parseInt(b, 2)));

      // Example iv/salt placeholders (replace with actual extraction if included)
      const iv = encryptedMetaBytes.slice(0, 16);
      const salt = encryptedMetaBytes.slice(16, 32);

      const decryptedBinary = await decryptData(encryptedMetaBytes, password, iv, salt);
      const metadataText = binaryToText(decryptedBinary);

      console.log('Decrypted metadata:', metadataText);
      setDecryptedMessage(metadataText);

    } catch (err) {
      setError('Failed to decrypt: ' + err.message);
    }
  };

  useEffect(() => {
    if (!file) return;

    ToBits(file)
      .then(({ frames: extractedFrames }) => {
        setFrames(extractedFrames);
      })
      .catch(err => setError(err.message));
  }, [file]);

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      <input
        type="password"
        placeholder="Enter decryption password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {file && password && (
        <button onClick={handleDecryptClick}>Decrypt Metadata</button>
      )}

      {decryptedMessage && (
        <div className="decrypted-message">
          <h2>Decrypted Metadata</h2>
          <pre>{decryptedMessage}</pre>
        </div>
      )}

      {error && <p className="error">{error}</p>}
    </div>
  );
}
