// components/GifFrameViewer.jsx
import { useEffect, useState } from "react";
import gifFrames from "gif-frames";

function GifFrameViewer({ file }) {
  const [frameURLs, setFrameURLs] = useState([]);

  useEffect(() => {
    if (!file) return;

    const objectURL = URL.createObjectURL(file);

    gifFrames({ url: objectURL, frames: 'all', outputType: 'canvas' })
      .then((frames) => {
        const urls = frames.map((frame) => frame.getImage().toDataURL("image/png"));
        setFrameURLs(urls);
      })
      .catch((err) => console.error("GIF FRAME ERROR:", err));

    return () => URL.revokeObjectURL(objectURL);
  }, [file]);

  return (
    <div style={{ marginTop: "20px" }}>
      <h2>GIF Frames</h2>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
        {frameURLs.map((url, idx) => (
          <img
            key={idx}
            src={url}
            alt={`frame-${idx}`}
            style={{
              border: "1px solid #ccc",
              maxWidth: "150px",
              imageRendering: "pixelated"
            }}
          />
        ))}
      </div>
    </div>
  );
}

export default GifFrameViewer;
