// GifFrameViewer.js
import { useState, useEffect } from "react";
import { parseGIF, decompressFrames } from "gifuct-js";

export default function GifFrameViewer({ file }) {
  const [framePreviews, setFramePreviews] = useState([]);

  useEffect(() => {
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      const arrayBuffer = reader.result;

      const gif = parseGIF(arrayBuffer);
      const frames = decompressFrames(gif, true);

      const previews = frames.map((frame) => {
        const canvas = document.createElement('canvas');
        canvas.width = frame.width;
        canvas.height = frame.height;
        const ctx = canvas.getContext('2d');
        const imageData = ctx.createImageData(frame.width, frame.height);
        imageData.data.set(frame.patch);
        ctx.putImageData(imageData, 0, 0);
        return canvas.toDataURL();
      });

      setFramePreviews(previews);
    };

    reader.readAsArrayBuffer(file);
  }, [file]);

  return (
    <div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)",
          gap: "15px",
        }}
      >
        {framePreviews.map((src, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "5px",
              textAlign: "center",
              background: "#fafafa",
            }}
          >
            <p style={{ marginBottom: "5px" }}>Frame {index}</p>
            <img
              src={src}
              alt={`Frame ${index}`}
              style={{ width: "100%", borderRadius: "4px" }}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
