import { useState } from "react";
import ToBits from "./ToBits";

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1];

function bitArrayToString(bits) {
	const bytes = [];
	for (let i = 0; i < bits.length; i += 8) {
		let byte = 0;
		for (let j = 0; j < 8; j++) {
			byte = (byte << 1) | (bits[i + j] || 0);
		}
		bytes.push(byte);
	}
	return new TextDecoder("utf-8").decode(new Uint8Array(bytes));
}

// --- Extract relevant bits from GIF frames ---
function extractRelevantBitsFromFrames(frames) {
	const allBits = [];
	for (const frame of frames) {
		const patch = frame.patch;
		for (let i = 0; i < patch.length; i += 4) {
			allBits.push(patch[i + 2] & 1); // LSB of blue channel
		}
	}

	// Check header
	for (let i = 0; i < HEADER_BITS.length; i++) {
		if (allBits[i] !== HEADER_BITS[i])
			throw new Error("Header not found. GIF may not be encrypted or is corrupt.");
	}

	// --- Extract metadata length (first 32 bits after header) ---
	const metadataLengthBits = allBits.slice(HEADER_BITS.length, HEADER_BITS.length + 32);
	const metadataLength = parseInt(metadataLengthBits.join(""), 2);

	const metadataStart = HEADER_BITS.length + 32;
	const metadataEnd = metadataStart + metadataLength;

	const metadataBits = allBits.slice(metadataStart, metadataEnd);
	const metadataText = bitArrayToString(metadataBits);
	const metadata = JSON.parse(metadataText);

	// --- Extract payload length (first 32 bits of payload) ---
	const payloadLengthBits = allBits.slice(metadataEnd, metadataEnd + 32);
	const payloadLength = parseInt(payloadLengthBits.join(""), 2);

	const messageBits = allBits.slice(metadataEnd + 32, metadataEnd + 32 + payloadLength);

	// Return all relevant bits, message bits, and metadata
	const allRelevantBits = allBits.slice(0, metadataEnd + 32 + payloadLength);
	return { allRelevantBits, messageBits, metadata };
}

export default function Decryption() {
	const [gifFile, setGifFile] = useState(null);
	const [frames, setFrames] = useState([]);
	const [error, setError] = useState(null);

	const handleGIFChange = (e) => {
		const file = e.target.files[0];
		if (!file) return;
		setGifFile(file);

		ToBits(file)
			.then(({ frames }) => setFrames(frames))
			.catch((err) => setError(err.message));
	};

	const handleDownloadBits = () => {
		if (!frames.length) return;

		try {
			const { allRelevantBits, messageBits } = extractRelevantBitsFromFrames(frames);

			// --- Download full embedded bits ---
			const fullBitString = allRelevantBits.join("");
			const blobFull = new Blob([fullBitString], { type: "text/plain" });
			const aFull = document.createElement("a");
			aFull.href = URL.createObjectURL(blobFull);
			aFull.download = "gif_embedded_bits.txt";
			aFull.click();

			// --- Download message bits only ---
			const messageBitString = messageBits.join("");
			const blobMessage = new Blob([messageBitString], { type: "text/plain" });
			const aMessage = document.createElement("a");
			aMessage.href = URL.createObjectURL(blobMessage);
			aMessage.download = "gif_message_bits.txt";
			aMessage.click();

			// --- Convert message bits to UTF-8 text and download ---
			const messageBytes = [];
			for (let i = 0; i < messageBits.length; i += 8) {
				let byte = 0;
				for (let j = 0; j < 8; j++) {
					byte = (byte << 1) | (messageBits[i + j] || 0);
				}
				messageBytes.push(byte);
			}
			const messageText = new TextDecoder("utf-8").decode(new Uint8Array(messageBytes));
			const blobText = new Blob([messageText], { type: "text/plain" });
			const aText = document.createElement("a");
			aText.href = URL.createObjectURL(blobText);
			aText.download = "gif_message_text.txt";
			aText.click();

			console.log("Full bits downloaded:", allRelevantBits.length);
			console.log("Message bits downloaded:", messageBits.length);
			console.log("Message text downloaded.");
		} catch (err) {
			setError(err.message);
		}
	};

	return (
		<div>
			<h2>Decrypt GIF Binary Data</h2>
			<input type="file" accept="image/gif" onChange={handleGIFChange} />
			{gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
			{frames.length > 0 && (
				<button onClick={handleDownloadBits}>
					Download GIF Bits & Message
				</button>
			)}
			{error && <p className="error">{error}</p>}
		</div>
	);
}
