// DecryptMetadataSection.js
import { useEffect, useState } from "react";
import ToBits from "./ToBits";
import '../stylesheets/Frames.css';

const decodeMetadata = (frames, metadataLength) => {
  let bits = '';
  for (let i = 0; i < metadataLength; i++) {
    const frame = frames[i % frames.length]; // loop if metadata longer than frames
    bits += (frame.patch[2] & 1).toString(); // first pixel, blue channel
  }
  return bits;
};

// Convert binary string to JSON string
const binaryToString = (binaryStr) => {
  let str = '';
  for (let i = 0; i < binaryStr.length; i += 8) {
    str += String.fromCharCode(parseInt(binaryStr.slice(i, i + 8), 2));
  }
  return str;
};

export default function DecryptMetadata({ metadataLength }) {
  const [frames, setFrames] = useState([]);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [metadata, setMetadata] = useState(null);
  const [error, setError] = useState(null);

  const handleGIFChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (file.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setError(null);
    setFile(file);
    setFileName(file.name);
  };

  useEffect(() => {
    if (!file) return;

    ToBits(file)
      .then(({ frames: extractedFrames }) => {
        setFrames(extractedFrames);

        const metadataBits = decodeMetadata(extractedFrames, metadataLength);
        try {
          const metadataStr = binaryToString(metadataBits);
          const parsed = JSON.parse(metadataStr);
          setMetadata(parsed);
        } catch (err) {
          setError("Failed to parse metadata: " + err.message);
        }
      })
      .catch(err => setError(err.message));
  }, [file, metadataLength]);

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      {metadata && (
        <div>
          <h2>Extracted Metadata</h2>
          <pre>{JSON.stringify(metadata, null, 2)}</pre>
        </div>
      )}

      {error && <p className="error">{error}</p>}
    </div>
  );
}
