// Decryption.js
import { useState } from "react";
import ToBits from "./ToBits";
import "../stylesheets/Frames.css";
import "../stylesheets/Popup.css";

const HEADER_BITS = [1,0,1,0,1,0,1,1];
const PIXELS_PER_CHUNK = 500;

export default function Decryption() {
  const [gifFile, setGifFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataText, setMetadataText] = useState('');
  const [popupData, setPopupData] = useState(null);
  const [error, setError] = useState(null);

  // Handle GIF upload
  const handleGIFChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setGifFile(file);

    ToBits(file)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  // Convert bits array to string
  const bitsToString = (bits) => {
    let str = '';
    for (let i = 0; i < bits.length; i += 8) {
      const byte = bits.slice(i, i+8).join('');
      str += String.fromCharCode(parseInt(byte, 2));
    }
    return str;
  };

  // Extract metadata
  const handleExtractMetadata = () => {
    if (!frames.length) return;

    const firstFrame = frames[0];
    const headerLSBs = [];
    for (let i = 0; i < 8; i++) {
      headerLSBs.push(firstFrame.patch[i*4 + 2] & 1);
    }

    // Compare header bits
    let headerMatches = true;
    for (let i = 0; i < HEADER_BITS.length; i++) {
      if (headerLSBs[i] !== HEADER_BITS[i]) {
        headerMatches = false;
        break;
      }
    }

    if (!headerMatches) {
      console.warn("Header read from GIF:", headerLSBs);
      console.warn("Expected HEADER_BITS:", HEADER_BITS);
      setError("Header not found in GIF.");
      return;
    }

    // Extract all bits after header
    const allBits = [];
    for (let frameIndex = 0; frameIndex < frames.length; frameIndex++) {
      const patch = frames[frameIndex].patch;
      const startPixel = frameIndex === 0 ? HEADER_BITS.length : 0;
      for (let i = startPixel * 4; i < patch.length; i += 4) {
        allBits.push(patch[i + 2] & 1);
      }
    }

    // First 32 bits = metadata length
    const lengthBits = allBits.slice(0, 32);
    const metadataLength = parseInt(lengthBits.join(''), 2);

    // Metadata bits
    const metadataBits = allBits.slice(32, 32 + metadataLength);
    const metadataStr = bitsToString(metadataBits);

    setMetadataText(metadataStr);

    // Download metadata
    const blob = new Blob([metadataStr], { type: 'text/plain' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'metadata.txt';
    a.click();

    // Optional: show first frame RGBA preview
    const frame = frames[0];
    const pixels = frame.patch;
    const pixelArray = [];
    for (let i = 0; i < pixels.length; i += 4) {
      const r = pixels[i];
      const g = pixels[i + 1];
      const b = pixels[i + 2];
      const a = pixels[i + 3];
      const rgbaString = `rgba(${r}, ${g}, ${b}, ${a / 255})`;
      pixelArray.push({ rgba: rgbaString, values: [r,g,b,a] });
    }

    setPopupData({
      frameIndex: 0,
      pixels: pixelArray,
      visibleCount: PIXELS_PER_CHUNK
    });
  };

  // Lazy load pixels on scroll
  const handleScroll = (e) => {
    const target = e.target;
    if (target.scrollTop + target.clientHeight >= target.scrollHeight - 20) {
      setPopupData((prev) => prev ? {
        ...prev,
        visibleCount: Math.min(prev.visibleCount + PIXELS_PER_CHUNK, prev.pixels.length)
      } : prev);
    }
  };

  return (
    <div>
      <h2>Decrypt GIF Metadata</h2>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
      {frames.length > 0 && <button onClick={handleExtractMetadata}>Extract Metadata</button>}

      {metadataText && (
        <div>
          <p>Metadata:</p>
          <pre>{metadataText}</pre>
        </div>
      )}

      {popupData && (
        <div className="popup-overlay" onClick={() => setPopupData(null)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()} onScroll={handleScroll}>
            <h3>Frame {popupData.frameIndex} RGBA Values</h3>
            <div className="pixel-grid">
              {popupData.pixels
                .slice(0, popupData.visibleCount)
                .map((p, i) => (
                  <div key={i} className="pixel-item">
                    <div className="pixel-color" style={{ background: p.rgba }} />
                    <span>Pixel {i}: [{p.values.join(', ')}]</span>
                  </div>
                ))}
            </div>
          </div>
        </div>
      )}

      {error && <p className="error">{error}</p>}
    </div>
  );
}
