// toBinary.js

/**
 * Converts a string or a file to binary.
 * @param {string|File} input - The input to convert to binary.
 * @returns {string|Promise<string>} The binary representation of the input.
 */
async function toBinary(input) {
  if (typeof input === 'string') {
    return Array.from(input).map(char => char.charCodeAt(0).toString(2).padStart(8, '0')).join('');
  } else if (input instanceof File) {
    const arrayBuffer = await input.arrayBuffer();
    const uint8Array = new Uint8Array(arrayBuffer);
    return Array.from(uint8Array).map(byte => byte.toString(2).padStart(8, '0')).join('');
  } else {
    throw new Error('Invalid input type. Input must be a string or a File object.');
  }
}

// Export the function
export default toBinary;
