// Decryption.js
import { useState } from "react";
import ToBits from "./ToBits";

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1];

function bitArrayToString(bits) {
  let str = '';
  for (let i = 0; i < bits.length; i += 8) {
    let byte = 0;
    for (let j = 0; j < 8; j++) {
      byte = (byte << 1) | (bits[i + j] || 0);
    }
    str += String.fromCharCode(byte);
  }
  return str;
}

function bitArrayToUint8Array(bits) {
  const bytes = [];
  for (let i = 0; i < bits.length; i += 8) {
    let byte = 0;
    for (let j = 0; j < 8; j++) {
      byte = (byte << 1) | (bits[i + j] || 0);
    }
    bytes.push(byte);
  }
  return new Uint8Array(bytes);
}

function base64ToUint8Array(base64) {
  return Uint8Array.from(atob(base64), c => c.charCodeAt(0));
}

export default function Decryption() {
  const [gifFile, setGifFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [error, setError] = useState(null);

  const handleGIFChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setGifFile(file);

    ToBits(file)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  const handleExtractBinary = () => {
    if (!frames.length) return;

    const allBits = [];
    for (let frameIndex = 0; frameIndex < frames.length; frameIndex++) {
      const patch = frames[frameIndex].patch;
      for (let i = 0; i < patch.length; i += 4) {
        allBits.push(patch[i + 2] & 1); // LSB of blue channel
      }
    }

    // Check header
    for (let i = 0; i < HEADER_BITS.length; i++) {
      if (allBits[i] !== HEADER_BITS[i]) {
        setError("Header not found in GIF.");
        console.warn("Header read:", allBits.slice(0, HEADER_BITS.length));
        return;
      }
    }

    // Extract metadata length
    const metadataLengthBits = allBits.slice(HEADER_BITS.length, HEADER_BITS.length + 32);
    const metadataLength = parseInt(metadataLengthBits.join(''), 2);

    // Extract metadata and payload
    const metadataBits = allBits.slice(HEADER_BITS.length + 32, HEADER_BITS.length + 32 + metadataLength);
    const payloadBits = allBits.slice(HEADER_BITS.length + 32 + metadataLength);

    // Convert metadata bits to string and JSON
    const metadataText = bitArrayToString(metadataBits);
    let metadata;
    try {
      metadata = JSON.parse(metadataText);
    } catch (err) {
      setError("Failed to parse metadata JSON.");
      console.error(err);
      return;
    }

    // Download metadata for reading
    const metadataBlob = new Blob([metadataText], { type: 'text/plain' });
    const metadataA = document.createElement('a');
    metadataA.href = URL.createObjectURL(metadataBlob);
    metadataA.download = 'metadata.txt';
    metadataA.click();

    // Convert payload bits to Uint8Array ready for decryption
    const encryptedBytes = bitArrayToUint8Array(payloadBits);

    console.log("Metadata:", metadata);
    console.log("Encrypted payload length:", encryptedBytes.length);
    console.log("IV:", base64ToUint8Array(metadata.iv));
    console.log("Salt:", base64ToUint8Array(metadata.salt));

    // Optionally, download the payload for testing
    const payloadBlob = new Blob([encryptedBytes], { type: 'application/octet-stream' });
    const payloadA = document.createElement('a');
    payloadA.href = URL.createObjectURL(payloadBlob);
    payloadA.download = metadata.fileName || 'payload.bin';
    payloadA.click();
  };

  return (
    <div>
      <h2>Decrypt GIF Binary Data</h2>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
      {frames.length > 0 && <button onClick={handleExtractBinary}>Extract Binary Data</button>}
      {error && <p className="error">{error}</p>}
    </div>
  );
}
