  const [file, setFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [metadataText, setMetadataText] = useState('');
  const [error, setError] = useState(null);

  const handleGIFChange = (event) => {
    const gifFile = event.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setFile(gifFile);

    ToBits(gifFile)
      .then(({ frames }) => setFrames(frames))
      .catch(err => setError(err.message));
  };

  // Convert bits to string and stop after closing brace
  const bitsToStringUntilClosedBraces = (bits) => {
    let str = '';
    let braceCount = 0;
    let started = false;

    for (let i = 0; i < bits.length; i += 8) {
      const byte = bits.slice(i, i + 8).padEnd(8, '0');
      const charCode = parseInt(byte, 2);
      if (isNaN(charCode)) continue; // skip invalid bytes
      const char = String.fromCharCode(charCode);

      if (char === '{') {
        braceCount++;
        started = true;
      } else if (char === '}') {
        braceCount--;
      }

      if (started) str += char;

      if (started && braceCount === 0) break; // stop exactly after closing brace
    }

    return str;
  };

  const handleExtract = () => {
    if (!frames.length) return;

    // Search all frames for header
    let startBitIndex = -1;
    outer: 
    for (let f = 0; f < frames.length; f++) {
      const frame = frames[f];
      for (let i = 0; i < frame.patch.length; i++) {
        let bits = '';
        for (let j = 0; j < HEADER_PATTERN.length; j++) {
          if (i + j*4 >= frame.patch.length) break;
          bits += (frame.patch[i + j*4 + 2] & 1).toString();
        }
        if (bits === HEADER_PATTERN) {
          startBitIndex = f * frame.patch.length + i + HEADER_PATTERN.length*4; // start after header
          break outer;
        }
      }
    }

    if (startBitIndex === -1) {
      setError("Header not found in GIF.");
      return;
    }

    // Collect all bits starting from startBitIndex
    let bits = '';
    let totalPixels = frames.reduce((acc, frame) => acc + frame.patch.length, 0);
    for (let bitPos = startBitIndex; bitPos < totalPixels; bitPos += 4) {
      const frameIndex = Math.floor(bitPos / frames[0].patch.length);
      const pixelIndex = bitPos % frames[frameIndex].patch.length;
      bits += (frames[frameIndex].patch[pixelIndex + 2] & 1).toString();
    }

    const metadataString = bitsToStringUntilClosedBraces(bits);
    setMetadataText(metadataString);

    // Download metadata
    const blob = new Blob([metadataString], { type: "text/plain" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "extracted_metadata.txt";
    a.click();
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {file && <p>Uploaded GIF: {file.name}</p>}
      {frames.length > 0 && <button onClick={handleExtract}>Extract Metadata</button>}
      {metadataText && (
        <div>
          <p>Metadata:</p>
          <pre>{metadataText}</pre>
        </div>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
