import { useState } from "react";
import ToBits from "./ToBits";

const HEADER_BITS = [1, 0, 1, 0, 1, 0, 1, 1];

function bitArrayToUint8Array(bits) {
  const bytes = [];
  for (let i = 0; i < bits.length; i += 8) {
    let byte = 0;
    for (let j = 0; j < 8; j++) {
      byte = (byte << 1) | (bits[i + j] || 0);
    }
    bytes.push(byte);
  }
  return new Uint8Array(bytes);
}

export default function Decryption() {
  const [frames, setFrames] = useState([]);
  const [error, setError] = useState(null);

  const handleGIFChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      const { frames } = await ToBits(file);
      setFrames(frames);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleExtractPayload = () => {
    if (!frames.length) return;

    // 1️⃣ Read all LSBs
    const allBits = [];
    for (const frame of frames) {
      const patch = frame.patch;
      for (let i = 0; i < patch.length; i += 4) {
        allBits.push(patch[i + 2] & 1);
      }
    }

    // 2️⃣ Verify header
    for (let i = 0; i < HEADER_BITS.length; i++) {
      if (allBits[i] !== HEADER_BITS[i]) {
        setError("Header not found");
        console.warn("Read header:", allBits.slice(0, 8));
        return;
      }
    }

    // 3️⃣ Read metadata length
    const lengthBits = allBits.slice(8, 40);
    const metadataBitLength = parseInt(lengthBits.join(""), 2);

    // 4️⃣ Skip metadata
    const payloadStart = 40 + metadataBitLength;
    const payloadBits = allBits.slice(payloadStart);

    console.log("Payload bits length:", payloadBits.length);

    // 5️⃣ Export raw bitstream
    const bitBlob = new Blob([payloadBits.join("")], { type: "text/plain" });
    const bitA = document.createElement("a");
    bitA.href = URL.createObjectURL(bitBlob);
    bitA.download = "payload.bits.txt";
    bitA.click();

    // 6️⃣ Export binary bytes
    const payloadBytes = bitArrayToUint8Array(payloadBits);
    const binBlob = new Blob([payloadBytes], { type: "application/octet-stream" });
    const binA = document.createElement("a");
    binA.href = URL.createObjectURL(binBlob);
    binA.download = "payload.bin";
    binA.click();
  };

  return (
    <div>
      <h2>Extract Raw Payload (LSB)</h2>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {frames.length > 0 && (
        <button onClick={handleExtractPayload}>Extract Payload</button>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
}
