// ToBinary.js 
import { encryptData } from "./ToBlackBox";

function uint8ArrayToBitArray(bytes) {
  const bits = [];
  for (const byte of bytes) {
    for (let i = 7; i >= 0; i--) {
      bits.push((byte >> i) & 1);
    }
  }
  return bits;
}

function stringToBitArray(str) {
  const bits = [];
  for (let i = 0; i < str.length; i++) {
    const byte = str.charCodeAt(i);
    for (let j = 7; j >= 0; j--) { bits.push((byte >> j) & 1); }
  } return bits;
} function numberTo32BitArray(num) {
  const bits = [];
  for (let i = 31; i >= 0; i--) {
    bits.push((num >> i) & 1);
  }
  return bits;
}
// Helper: Convert Uint8Array to Base64 string 
function uint8ArrayToBase64(uint8Array) {
  return btoa(String.fromCharCode(...uint8Array));
}

export default async function ToBinary(file, password) {
  if (!file) throw new Error("No file provided");
  //if (!password || password.trim() === "") throw new Error("Password is required for encryption");
  const arrayBuffer = await file.arrayBuffer(); const bytes = new Uint8Array(arrayBuffer);

  // Encrypt raw bytes 
  //const { encryptedData, iv, salt } = await encryptData(bytes, password);
  let messageBits =uint8ArrayToBitArray(bytes)

  // Encrypted payload to bit array 
  //const payloadBits = uint8ArrayToBitArray(encryptedData);
  const payloadBits = messageBits;

  // Metadata object with Base64 encoding for binary data 
  const metadata = { 
    //iv: uint8ArrayToBase64(iv), 
    //salt: uint8ArrayToBase64(salt), 
    originalLength: bytes.length, 
    fileName: file.name, 
    fileType: file.type, };

  // Metadata to JSON string and then to bit array 
  const metadataStr = JSON.stringify(metadata); const metadataBits = stringToBitArray(metadataStr);

  // Add 32-bit length header at the start 
  const lengthBits = numberTo32BitArray(metadataBits.length);
  const fullMetadataBits = [...lengthBits, ...metadataBits];

  return { bits: payloadBits, metabits: fullMetadataBits, };
}
