//toBinary
//This function converts an uploaded file into a binary string representation
import { encryptData, decryptData } from "./ToBlackBox";

export default async function ToBinary(file, password, type) {
  if (!file) { throw new Error("No file provided"); }
  if (!password || password.trim() === "") { throw new Error("Password is required for encryption"); }

  if (type === "encrypt") {
    
    const arrayBuffer = await file.arrayBuffer();
    const bytes = new Uint8Array(arrayBuffer);

    let binary = '';
    for (let i = 0; i < bytes.length; i++) {
      binary += bytes[i].toString(2).padStart(8, '0');
    }

    const { encryptedData, iv, salt } = await encryptData(binary, password);
    const encryptedBinary = Array.from(encryptedData).map(byte => byte.toString(2).padStart(8, '0')).join('');

    return {
      bits: encryptedBinary,
      iv,
      salt,
      originalLength: binary.length,
      fileName: file.name,
      fileType: file.type,
    };
  }
}
