import { useState, useEffect } from "react";
import { parseGIF, decompressFrames } from "gifuct-js";
import '../stylesheets/Frames.css';

export default function DecryptSection() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [error, setError] = useState(null);
  const [metadata, setMetadata] = useState(null);

  const handleGIFChange = (e) => {
    const gifFile = e.target.files[0];
    if (!gifFile) return;
    if (gifFile.type !== 'image/gif') {
      setError("Please upload a valid GIF file");
      return;
    }
    setFile(gifFile);
    setFileName(gifFile.name);
    setError(null);
  };

  const extractMetadataBits = async (gifFile) => {
    const arrayBuffer = await gifFile.arrayBuffer();
    const gif = parseGIF(arrayBuffer);
    const frames = decompressFrames(gif, true);

    // Extract LSBs from first pixel of each frame
    let allBits = '';
    for (let i = 0; i < frames.length; i++) {
      allBits += (frames[i].patch[0] & 1).toString();
    }

    // Get metadata length from first 32 bits
    const lengthBits = allBits.slice(0, 32);
    const metadataLength = parseInt(lengthBits, 2);

    // Extract metadata bits
    const metadataBits = allBits.slice(32, 32 + metadataLength);

    console.log("Metadata length (bits):", metadataLength);
    console.log("Raw metadata bits:", metadataBits);

    // Helper: pad to whole bytes and convert bits -> Uint8Array
    const bitsToUint8Array = (bits) => {
      let s = bits;
      while (s.length % 8 !== 0) s += '0';
      const bytes = new Uint8Array(s.length / 8);
      for (let i = 0; i < bytes.length; i++) {
        const byte = s.slice(i * 8, i * 8 + 8);
        bytes[i] = parseInt(byte, 2);
      }
      return bytes;
    };

    const byteArray = bitsToUint8Array(metadataBits);
    let metadataString = '';
    try {
      metadataString = new TextDecoder().decode(byteArray);
    } catch (e) {
      console.warn('Failed to decode metadata as UTF-8:', e);
      metadataString = '';
    }

    console.log('Decoded metadata string:', metadataString);

    return { metadataBits, metadataString };
  };

  const handlePrintMetadata = () => {
    if (!file) return;
    extractMetadataBits(file)
      .then(({ metadataBits, metadataString }) => {
        console.log('--- Metadata ---');
        console.log('Bits:', metadataBits);
        console.log('String:', metadataString);
        setMetadata(metadataString);
      })
      .catch(err => setError(err.message));
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      {file && (
        <button onClick={handlePrintMetadata}>
          Print Metadata Bits to Console
        </button>
      )}

      {metadata && (
        <div style={{ marginTop: '8px' }}>
          <strong>Decoded Metadata:</strong>
          <div style={{ whiteSpace: 'pre-wrap', marginTop: '4px' }}>{metadata}</div>
        </div>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
