const handleDownloadAllBits = () => {
	if (!frames.length) return;

	try {
		const allBits = extractAllBitsFromFrames(frames);

		// Extract metadata length (first 32 bits after header)
		const metadataLengthBits = allBits.slice(HEADER_BITS.length, HEADER_BITS.length + 32);
		const metadataLength = parseInt(metadataLengthBits.join(""), 2);

		// Calculate where metadata ends
		const metadataEndIndex = HEADER_BITS.length + 32 + metadataLength;

		// Message bits: all bits after metadata
		const messageBits = allBits.slice(metadataEndIndex);

		// Convert full bits to string and download
		const fullBitString = allBits.join("");
		const blobFull = new Blob([fullBitString], { type: "text/plain" });
		const aFull = document.createElement("a");
		aFull.href = URL.createObjectURL(blobFull);
		aFull.download = "gif_embedded_bits.txt";
		aFull.click();

		// Convert message bits to string and download separately
		const messageBitString = messageBits.join("");
		const blobMessage = new Blob([messageBitString], { type: "text/plain" });
		const aMessage = document.createElement("a");
		aMessage.href = URL.createObjectURL(blobMessage);
		aMessage.download = "gif_message_bits.txt";
		aMessage.click();

		console.log("Full GIF bits downloaded:", allBits.length);
		console.log("Message bits downloaded:", messageBits.length);
	} catch (err) {
		setError(err.message);
	}
};


	return (
		<div>
			<h2>Decrypt GIF Binary Data</h2>
			<input type="file" accept="image/gif" onChange={handleGIFChange} />
			{gifFile && <p>Uploaded GIF: {gifFile.name}</p>}
			{frames.length > 0 && (
				<button onClick={handleDownloadAllBits}>
					Download All Embedded Bits
				</button>
			)}
			{error && <p className="error">{error}</p>}
		</div>
	);
}
