function toBinary(input) {
  if (typeof input === 'string') {
    return Array.from(input).map(char => char.charCodeAt(0).toString(2).padStart(8, '0')).join('');
  } else if (input instanceof File) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        const arrayBuffer = reader.result;
        const uint8Array = new Uint8Array(arrayBuffer);
        const binaryString = Array.from(uint8Array).map(byte => byte.toString(2).padStart(8, '0')).join('');
        resolve(binaryString);
      };
      reader.onerror = () => {
        reject(reader.error);
      };
      reader.readAsArrayBuffer(input);
    });
  } else {
    throw new Error('Invalid input type. Input must be a string or a File object.');
  }
}
