import { useEffect, useState } from "react";
import { encodeFromUpload } from "./Stego";
import "../stylesheets/Frames.css";

/* =========================
   FRAME PREVIEW GENERATION
========================= */

async function generateFramePreviews(frames, setFramePreviews) {
  const previews = [];

  for (const frame of frames) {
    const { width, height, patch, dims } = frame;

    // Full RGBA buffer
    const fullImage = new Uint8ClampedArray(width * height * 4);

    // Default transparent background
    fullImage.fill(0);

    const { top, left, width: pw, height: ph } = dims;

    // Copy patch into correct position
    for (let y = 0; y < ph; y++) {
      for (let x = 0; x < pw; x++) {
        const srcIndex = (y * pw + x) * 4;
        const dstIndex =
          ((y + top) * width + (x + left)) * 4;

        fullImage[dstIndex]     = patch[srcIndex];
        fullImage[dstIndex + 1] = patch[srcIndex + 1];
        fullImage[dstIndex + 2] = patch[srcIndex + 2];
        fullImage[dstIndex + 3] = patch[srcIndex + 3];
      }
    }

    // Draw to canvas
    const canvas = document.createElement("canvas");
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext("2d");

    const imageData = new ImageData(fullImage, width, height);
    ctx.putImageData(imageData, 0, 0);

    previews.push(canvas.toDataURL("image/png"));
  }

  setFramePreviews(previews);
}


/* =========================
   PIXEL INSPECTOR
========================= */

function handleFrameClick(frame, index, setSelectedFrame) {
  let output = "";
  const pixelsPerRow = 6;

  for (let i = 0; i < frame.patch.length; i += 4 * pixelsPerRow) {
    for (let j = 0; j < pixelsPerRow; j++) {
      const idx = i + j * 4;
      if (idx >= frame.patch.length) break;

      const r = String(frame.patch[idx]).padStart(3, "_");
      const g = String(frame.patch[idx + 1]).padStart(3, "_");
      const b = String(frame.patch[idx + 2]).padStart(3, "_");
      const a = String(frame.patch[idx + 3]).padStart(3, "_");

      output += `[${r},${g},${b},${a}] `;
    }
    output += "\n";
  }

  setSelectedFrame({ frame: index + 1, bits: output });
}

/* =========================
   MAIN COMPONENT
========================= */

export default function GifSection() {
  const [gifFile, setGifFile] = useState(null);
  const [payloadFile, setPayloadFile] = useState(null);
  const [frames, setFrames] = useState([]);
  const [framePreviews, setFramePreviews] = useState([]);
  const [selectedFrame, setSelectedFrame] = useState(null);
  const [error, setError] = useState(null);

  /* =========================
     GENERATE PREVIEWS
  ========================= */

  useEffect(() => {
    if (frames.length > 0) {
      generateFramePreviews(frames, setFramePreviews);
    }
  }, [frames]);

  /* =========================
     HANDLERS
  ========================= */

  const handleGifUpload = (e) => {
    const file = e.target.files[0];
    if (!file || file.type !== "image/gif") {
      setError("Please upload a valid GIF file.");
      return;
    }
    setError(null);
    setGifFile(file);
  };

  const handlePayloadUpload = (e) => {
    setPayloadFile(e.target.files[0]);
  };

  const handleEncode = async () => {
    try {
      const encodedFrames = await encodeFromUpload(gifFile, payloadFile);
      setFrames(encodedFrames);
    } catch (err) {
      setError(err.message);
    }
  };

  /* =========================
     RENDER
  ========================= */

  return (
    <div>
      <h2>GIF Steganography</h2>

      <input type="file" accept="image/gif" onChange={handleGifUpload} />
      <input type="file" onChange={handlePayloadUpload} />

      <button
        disabled={!gifFile || !payloadFile}
        onClick={handleEncode}
      >
        Encode Payload
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {frames.length > 0 && (
        <div className="frame-container">
          <p>Frames encoded: {frames.length}</p>

          <div className="frames">
            {framePreviews.map((src, i) => (
              <div key={i} className="frame">
                <p>Frame {i + 1}</p>
                <img
                  src={src}
                  className="canvas"
                  onClick={() =>
                    handleFrameClick(frames[i], i, setSelectedFrame)
                  }
                />
              </div>
            ))}
          </div>
        </div>
      )}

      {selectedFrame && (
        <div className="modal">
          <div className="modal-content">
            <h3>Frame {selectedFrame.frame}</h3>
            <pre>{selectedFrame.bits}</pre>
            <button onClick={() => setSelectedFrame(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
