import { useState, useEffect } from "react";
import ToBits from "./ToBits";
import FromBinary from "./ToExtract";
import '../stylesheets/Frames.css';

const decryptMetadata = (frames) => {
  let bits = '';
  for (let i = 0; i < frames.length; i++) {
    bits += (frames[i].patch[2] & 1).toString();
  }
  return bits;
};

const extractPayloadBits = (frames, metadataLengthBits = 32, payloadLength = null) => {
  let bits = '';
  let bitIndex = 0;

  for (let i = 0; i < frames.length; i++) {
    const patch = frames[i].patch;
    for (let j = 0; j < patch.length; j += 4) {
      // Skip first pixel of first frame (used for metadata)
      if (i === 0 && j === 0) continue;
      bits += (patch[j + 2] & 1).toString();
      if (payloadLength && bits.length >= payloadLength) break;
    }
    if (payloadLength && bits.length >= payloadLength) break;
  }

  return bits;
};

export default function DecryptSection() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [frames, setFrames] = useState([]);
  const [error, setError] = useState(null);
  const [password, setPassword] = useState('');
  const [metadataBits, setMetadataBits] = useState('');
  const [decryptedFile, setDecryptedFile] = useState(null);

  const handleGIFChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (file.type !== 'image/gif') {
      setError('Please upload a valid GIF file.');
      return;
    }
    setError(null);
    setFile(file);
    setFileName(file.name);
  };

  const handleDecrypt = async () => {
    try {
      // Extract metadata bits from frames
      const metaBits = decryptMetadata(frames);

      // Get metadata length (first 32 bits)
      const metaLength = parseInt(metaBits.slice(0, 32), 2);

      // Extract payload bits
      const payloadBits = extractPayloadBits(frames, 32, metaLength);

      // Decrypt file
      const { blob, fileName } = await FromBinary(payloadBits, metaBits, password);

      // Create download link
      const url = URL.createObjectURL(blob);
      setDecryptedFile({ url, name: fileName });

    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    if (!file) return;
    ToBits(file)
      .then(({ frames: extractedFrames }) => setFrames(extractedFrames))
      .catch(err => setError(err.message));
  }, [file]);

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGIFChange} />
      {fileName && <p>Uploaded GIF: {fileName}</p>}

      <input
        type="password"
        placeholder="Enter decryption password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {file && password && frames.length > 0 && (
        <button onClick={handleDecrypt}>Decrypt Message</button>
      )}

      {decryptedFile && (
        <div>
          <a href={decryptedFile.url} download={decryptedFile.name}>
            Download Decrypted File
          </a>
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
