// GifMetadataReader.jsx
import { useState } from "react";

export default function GifMetadataReader() {
  const [fileName, setFileName] = useState("");

  // Function to convert GIF file to binary
  const gifToBinary = async (file) => {
    const arrayBuffer = await file.arrayBuffer();
    const bytes = new Uint8Array(arrayBuffer);

    let binary = "";
    for (let i = 0; i < bytes.length; i++) {
      binary += bytes[i].toString(2).padStart(8, "0");
    }

    return binary;
  };

  // Function to extract and print metadata from binary
  const printMetadataFromBinary = (binary) => {
    if (!binary || binary.length < 32) {
      console.error("Binary data too short for metadata header");
      return;
    }

    const lengthBits = binary.slice(0, 32);
    const metadataLength = parseInt(lengthBits, 2);

    if (binary.length < 32 + metadataLength) {
      console.error(
        `Metadata length mismatch! Header says ${metadataLength} bits, but only ${
          binary.length - 32
        } bits available.`
      );
      return;
    }

    const metadataBits = binary.slice(32, 32 + metadataLength);
    let metadataStr = "";
    for (let i = 0; i < metadataBits.length; i += 8) {
      const byte = metadataBits.slice(i, i + 8);
      metadataStr += String.fromCharCode(parseInt(byte, 2));
    }

    try {
      const metadata = JSON.parse(metadataStr);
      console.log("Decoded Metadata:", metadata);
    } catch (err) {
      console.error("Failed to parse metadata JSON:", err.message);
    }
  };

  // File input change handler
  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setFileName(file.name);

    try {
      const binary = await gifToBinary(file);
      console.log(`GIF "${file.name}" converted to binary, total bits: ${binary.length}`);
      printMetadataFromBinary(binary);
    } catch (err) {
      console.error("Failed to process GIF file:", err);
    }
  };

  return (
    <div>
      <input type="file" accept=".gif" onChange={handleFileChange} />
      {fileName && <p>Selected file: {fileName}</p>}
    </div>
  );
}
