import React, { useState } from 'react';
import { gifToBinaryString } from './Converter';

export default function GifUploader({ onGifUpload }) {
  const [file, setFile] = useState(null);

  const handleGifUpload = async () => {
    if (!file) return alert("Select a GIF file");

    const { binary, file: gifFile } = await gifToBinaryString(file);

    // Send binary and file to parent / encoder
    onGifUpload(binary, gifFile);
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={e => setFile(e.target.files[0])} />
      <button onClick={handleGifUpload}>Upload GIF</button>
    </div>
  );
}
