// ReadMetadataFromUpload.js
import printMetadataFromBinary from './ToExtract.js';

/**
 * Sets up a file input listener to read a file and print its metadata
 * @param {string} inputId - The id of the <input type="file"> element
 */
export function setupMetadataReader(inputId) {
  const fileInput = document.getElementById(inputId);
  if (!fileInput) {
    console.error(`No input element found with id "${inputId}"`);
    return;
  }

  fileInput.addEventListener('change', async (event) => {
    const file = event.target.files[0];
    if (!file) {
      console.warn("No file selected");
      return;
    }

    try {
      // Read file as ArrayBuffer
      const arrayBuffer = await file.arrayBuffer();
      const bytes = new Uint8Array(arrayBuffer);

      // Convert file bytes to binary string
      let binary = '';
      for (let i = 0; i < bytes.length; i++) {
        binary += bytes[i].toString(2).padStart(8, '0');
      }

      // Print metadata to console
      printMetadataFromBinary(binary);
    } catch (err) {
      console.error("Failed to read or process file:", err);
    }
  });
}
